"use strict";for(var smoothScrollTrigger=document.querySelectorAll('a[href^="#"]'),_loop=function(o){smoothScrollTrigger[o].addEventListener("click",function(e){e.preventDefault();e=smoothScrollTrigger[o].getAttribute("href"),e=document.getElementById(e.replace("#","")).getBoundingClientRect().top+window.pageYOffset-60;window.scrollTo({top:e,behavior:"smooth"})})},i=0;i<smoothScrollTrigger.length;i++)_loop(i);